const SidebarArtikel = () => {
  const [activeArt, setActiceArt] = useState(6);
  return (
    <>
      <ul className={`absolute w-full top-0 bg-white overflow-y-auto ${activeArt === 0 ? 'hidden' : "absolute w-full top-0 bg-white overflow-y-auto"}`}>
        {
          artikel.map((items, i) => (
            <li key={items.id} className='w-full'>
              <button onClick={() => setActiceArt(i)} className={`${activeArt === i ? "flex w-full h-[60px] items-center p-2 text-base font-normal text-gray-900 rounded-[10px] bg-[rgba(5,104,142,0.1)]" : "flex w-full h-[60px] items-center p-2 text-base font-normal text-gray-900 rounded-[10px] hover:bg-[rgba(5,104,142,0.1)]"}`}>
                <div className={`w-10 h-10 flex justify-center items-center ${i === activeArt ? "stroke-white bg-[#05B1A1]" : "stroke-black border-solid border-[#A3A3A3] border-[0.75px]"}`}>
                  {items.icon}
                </div>
                <span className="ml-3">{items.title}</span>
                <h1>{activeArt}</h1>
              </button>
            </li>
          ))
        }
      </ul>
    </>
  )
}

export default SidebarArtikel